    userModel.findOneAndRemove({
            username: req.query.username
        })
        .then(doc => {
            if (doc == null) {
                res.status(500).json({
                    status: "error",
                    message: "User not found.",
                    data: null
                })
            } else {
                res.status(200).json({
                    status: "success",
                    message: "Record deleted successfully.",
                    data: doc
                })
            }
        })
        .catch(err => {
            res.status(500).json(err);
        });
});

module.exports = router;

